app.controller('RecordEditController', ['$scope', '$location', '$routeParams', '$q', 'ArtistService', 'RecordService', 'ArtistService',
    function ($scope, $location, $routeParams, $q, ArtistService , RecordService , ArtistService) {

    	//manage create and save
		$scope.external = {};
		
    	if ($routeParams.id != 'new')
    	{
        	$scope.id = $routeParams.id;
        	$scope.obj = RecordService.get({_id: $scope.id});
        	
    	}
    	else{
    		$scope.obj = new RecordService();
        	
    	}
    	
    	$scope.save = function(){
    		$scope.obj.$save().then(function(data){
    			$scope.obj=data;
        		$location.path('/records/');
    		});
    	}
    	
    	$scope.remove = function(){
    		RecordService.remove({_id: $scope.obj._id}).$promise.then(function(){
				$('#removeModal').modal('hide');
				$('.modal-backdrop').remove();
				$('.modal-open').removeClass("modal-open");
        		$location.path('/records/');
    		});
    	}
    	
    		
        //manage relation artist
        		
    	$scope.list_Artist = ArtistService.query();

}]);